import React from "react";
import Input from "./Input";

export default function Mains({ meals }) {

  return (
    <section className="mains">
      {meals.map((meal, index) => (
      <div className="col-md">
				          <div className="card mb-6 shadow-sm">
                  <img className="img-fluid" src="https://cdn.pixabay.com/photo/2016/11/06/23/16/breakfast-1804436_960_720.jpg" />
				            <div className="card-body">
                    <h3 className="mains-name">{meal.name}</h3>
				              <p className="card-text">{meal.descripcion}</p>
				              <div className="d-flex justify-content-between align-items-center">
				                <div className="btn-group">
                        <Input type="mains" name={meal.name} index={index} />
				                  <button type="button" className="btn btn-sm btn-outline-secondary">Leer</button>

				                </div>

				              </div>
				            </div>
				          </div>
				        </div>
      ))}
    </section>
  );
}
