import React from "react";
import Input from "./Input";

export default function Mains({ meals }) {
  const styleButton={
    background: '#ea8118',
    borderColor:'#ea8118'
  }

  const styleInput={
    width:40
  }
  return (
    <section className="mains">
      {meals.map((meal, index) => (
        <article className="menu-item" key={index}>
          <h3 className="mains-name">{meal.name}</h3>
          <strong className="mains-price">${meal.price}</strong>
          <p className="mains-description">{meal.description}</p>
          <button type="button" className="btn btn-sm btn-outline-secondary">-</button>
          <Input type="mains" name={meal.name} index={index} style={styleInput}/>
          <button type="button" name={meal.name} className="btn btn-sm btn-secondary" style={styleButton}>+</button>

        </article>
      ))}
    </section>
  );
}
